    private void bt_parcourirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_parcourirActionPerformed
        JFileChooser file = new JFileChooser();
        file.setCurrentDirectory(new File(System.getProperty("user.home")));
        // filtrer les fichiers
        FileNameExtensionFilter filter = new FileNameExtensionFilter("*.Images","jpg","png");
        file.addChoosableFileFilter(filter);
        int res = file.showSaveDialog(null);
        // si l'utilisateur clique sur enregistrer dnas JFileChooser
        if(res == JFileChooser.APPROVE_OPTION){
            File selFile = file.getSelectedFile();
            String path = selFile.getAbsolutePath();
            lbl_image.setIcon(resize(path));
        }
    }//GEN-LAST:event_bt_parcourirActionPerformed

